import React, { useState, useEffect } from "react";
import {
  Box,
  Container,
  VStack,
  Input,
  Button,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  useToast,
  Heading,
  InputGroup,
  InputRightElement,
} from "@chakra-ui/react";
import { db } from "../config/firebase";
import { useAuth } from "../contexts/AuthContext";
import { collection, addDoc, query, where, getDocs } from "firebase/firestore";
import yahooFinance from "yahoo-finance2";

function Dashboard() {
  const [stockSymbol, setStockSymbol] = useState("");
  const [userStocks, setUserStocks] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const { currentUser } = useAuth();
  const toast = useToast();

  useEffect(() => {
    fetchUserStocks();
  }, []);

  const fetchUserStocks = async () => {
    try {
      const q = query(
        collection(db, "stocks"),
        where("userId", "==", currentUser.uid)
      );
      const querySnapshot = await getDocs(q);
      const stocks = [];
      querySnapshot.forEach((doc) => {
        stocks.push({ id: doc.id, ...doc.data() });
      });
      setUserStocks(stocks);
    } catch (error) {
      console.error("Error fetching stocks:", error);
    }
  };

  const handleAddStock = async () => {
    if (!stockSymbol) return;
    setIsLoading(true);

    try {
      const quote = await yahooFinance.quote(stockSymbol);

      await addDoc(collection(db, "stocks"), {
        userId: currentUser.uid,
        symbol: stockSymbol.toUpperCase(),
        purchasePrice: quote.regularMarketPrice,
        purchaseDate: new Date().toISOString(),
      });

      toast({
        title: "Stock Added",
        description: `Successfully added ${stockSymbol.toUpperCase()} to your portfolio`,
        status: "success",
        duration: 3000,
        isClosable: true,
      });

      setStockSymbol("");
      fetchUserStocks();
    } catch (error) {
      toast({
        title: "Error",
        description: "Invalid stock symbol or API error",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    }

    setIsLoading(false);
  };

  return (
    <Container maxW="container.lg" py={10}>
      <VStack spacing={8}>
        <Heading>Your Stock Dashboard</Heading>

        <Box w="100%">
          <InputGroup size="md">
            <Input
              value={stockSymbol}
              onChange={(e) => setStockSymbol(e.target.value.toUpperCase())}
              placeholder="Enter stock symbol (e.g., AAPL)"
            />
            <InputRightElement width="4.5rem">
              <Button
                h="1.75rem"
                size="sm"
                onClick={handleAddStock}
                isLoading={isLoading}
              >
                Add
              </Button>
            </InputRightElement>
          </InputGroup>
        </Box>

        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Symbol</Th>
              <Th>Purchase Price</Th>
              <Th>Purchase Date</Th>
            </Tr>
          </Thead>
          <Tbody>
            {userStocks.map((stock) => (
              <Tr key={stock.id}>
                <Td>{stock.symbol}</Td>
                <Td>${stock.purchasePrice.toFixed(2)}</Td>
                <Td>{new Date(stock.purchaseDate).toLocaleDateString()}</Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </VStack>
    </Container>
  );
}

export default Dashboard;
