import React from "react";
import { Box, Flex, Button, Heading, Spacer } from "@chakra-ui/react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

function Navbar() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate("/login");
    } catch (error) {
      console.error("Failed to log out:", error);
    }
  };

  return (
    <Box bg="gray.100" px={4} py={4}>
      <Flex alignItems="center">
        <Heading size="md">UofT Stock Trading</Heading>
        <Spacer />
        {currentUser && (
          <>
            <Button as={Link} to="/dashboard" mx={2} variant="ghost">
              Dashboard
            </Button>
            <Button as={Link} to="/leaderboard" mx={2} variant="ghost">
              Leaderboard
            </Button>
            <Button onClick={handleLogout} mx={2}>
              Logout
            </Button>
          </>
        )}
      </Flex>
    </Box>
  );
}

export default Navbar;
