import React from "react";
import {
  Box,
  Container,
  Heading,
  Text,
  VStack,
  Button,
  useColorModeValue,
  Icon,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  StatArrow,
} from "@chakra-ui/react";
import { Link } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { FaChartLine, FaTrophy, FaUserCircle } from "react-icons/fa";

function Dashboard() {
  const { currentUser } = useAuth();
  const bgColor = useColorModeValue("white", "gray.800");
  const borderColor = useColorModeValue("gray.200", "gray.600");

  const stats = [
    {
      label: "Portfolio Value",
      value: "$10,245.50",
      change: 12.5,
      icon: FaChartLine,
    },
    {
      label: "Current Rank",
      value: "#5",
      helpText: "Top 10%",
      icon: FaTrophy,
    },
    {
      label: "Active Since",
      value: new Date().toLocaleDateString(),
      helpText: "Trading Account",
      icon: FaUserCircle,
    },
  ];

  return (
    <Container maxW="container.lg" py={10}>
      <VStack spacing={8} align="stretch">
        <Box textAlign="center">
          <Heading
            bgGradient="linear(to-r, blue.400, purple.500)"
            bgClip="text"
            fontSize={{ base: "2xl", md: "4xl" }}
            fontWeight="extrabold"
            mb={2}
          >
            Welcome Back!
          </Heading>
          <Text fontSize="lg" color="gray.500">
            {currentUser?.email}
          </Text>
        </Box>

        <SimpleGrid columns={{ base: 1, md: 3 }} spacing={6}>
          {stats.map((stat, index) => (
            <Box
              key={index}
              bg={bgColor}
              p={6}
              borderRadius="lg"
              boxShadow="xl"
              border="1px"
              borderColor={borderColor}
              transition="all 0.2s"
              _hover={{ transform: "translateY(-2px)", boxShadow: "2xl" }}
            >
              <VStack spacing={3} align="stretch">
                <Icon as={stat.icon} w={6} h={6} color="blue.500" />
                <Stat>
                  <StatLabel fontSize="sm" color="gray.500">
                    {stat.label}
                  </StatLabel>
                  <StatNumber fontSize="2xl" fontWeight="bold">
                    {stat.value}
                  </StatNumber>
                  {stat.change && (
                    <StatHelpText>
                      <StatArrow
                        type={stat.change > 0 ? "increase" : "decrease"}
                      />
                      {Math.abs(stat.change)}%
                    </StatHelpText>
                  )}
                  {stat.helpText && (
                    <StatHelpText>{stat.helpText}</StatHelpText>
                  )}
                </Stat>
              </VStack>
            </Box>
          ))}
        </SimpleGrid>

        <Box
          bg={bgColor}
          p={8}
          borderRadius="lg"
          boxShadow="xl"
          border="1px"
          borderColor={borderColor}
          textAlign="center"
        >
          <VStack spacing={4}>
            <Heading size="md">Ready to Trade?</Heading>
            <Text color="gray.500">
              Check out the leaderboard to see how you compare with other
              traders
            </Text>
            <Button
              as={Link}
              to="/leaderboard"
              colorScheme="blue"
              size="lg"
              _hover={{ transform: "translateY(-2px)" }}
              transition="all 0.2s"
            >
              View Leaderboard
            </Button>
          </VStack>
        </Box>
      </VStack>
    </Container>
  );
}

export default Dashboard;
